import './index.css';
import TextAreaChat from './components/layout/TextAreaChat';
import TextAreaReadOnly from './components/layout/TextAreaReadOnly';

function App() {
  return (
    <Router>
      <div id="root">
        <Navbar />
        <div className="container">
          <TextAreaReadOnly messages={messages} /> {/* Pass messages state to TextAreaReadOnly */}
          <TextAreaChat />
        </div>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
